import React from 'react';
import {useState} from 'react';
import { Link } from 'react-router-dom';
import { authApi } from 'api';
import styled from "styled-components";

const Container = styled.div`
    background-color: #fafafa;
    width: 100%;
    height: calc(100vh - 60px);

    display: flex;
    justify-content: center;
    align-items: center;
`;
const SignupBox = styled.div`
    background-color: #fafafa;
    box-shadow: 0px 0px 3px 1px gray;

    width: 350px;
    height:480px;
    border-radius:20px;
    padding:0 40px 20px 40px;
    box-sizing: border-box;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
`;
const SignupTitle = styled.div`
    font-size: 35px;
    font-weight: 600px;
    font-style: italic;
    padding-top: 20px;
    margin-bottom: 30px;
`;
const SignupForm = styled.form`
    display: flex;
    flex-direction: column;
    justify-content: center;
    width:100%;
`;
const FormInput = styled.input`
    height: 35px;
    border: 2px solid gray;
    border-radius: 5px;
    text-align: center;
    &:nth-child(1){
        width: 75%;
        border-right: none;
        border-top-right-radius: 0;
        border-bottom-right-radius: 0;
    }
`;
const ErrorMessage = styled.div`
    height: 12px;
    margin: 5px 0;
    font-size: 12px;
    color:red;
    text-align: center;
`;
const FormBtn = styled.button`
    cursor: pointer;
    height: 35px;
    background-color: #fafafa;
    color: #0095f6;
    border:2px solid #0095f6;
    border-radius:5px;
    &:hover{
        color: #fafafa;
        background-color: #0095f6;
        box-shadow: 0px 0px 1px 1px #0095f6;
    }
`;
const DupCheckBtn = styled.button`
    background-color: #fafafa;
    color: #0095f6;

    width: 25%;
    height: 35px;
    border: 2px solid gray;
    border-left: none;
    border-radius: 5px;
    border-top-left-radius: 0;
    border-bottom-left-radius: 0;
    cursor: pointer;
    &:hover{
        color: #fafafa;
        background-color: #0095f6;
        border: 1px solid #0095f6;
        box-shadow: 0px 0px 1px 1px #0095f6;
    }
`;

const Signup = (props) => {
    const [state, setState] = useState({
        username: null,
        password: null,
        name:null,
        email:null
    });
    const [valid, setValid] = useState({
        username: false,
        name: false,
        password: false,
        email: false
    });
    const [error, setError] = useState({
        username: null,
        password: null,
        name:null,
        email:null
    });

    const onChange = (e) => {
        const { title, value } = e.target;
        setState({
            ...state,
            [title]: value  
        })
    }

    console.log(state);

    const checkPassword = (e) => {

    }

    return (
        <Container>
            <SignupBox>
                <SignupTitle>Sign-up</SignupTitle>
                <SignupForm>
                    <div>
                        <FormInput type="text" onChange={onChange} title="username" placeholder="                 Username" />
                        <DupCheckBtn>중복검사</DupCheckBtn>
                    </div>
                    <ErrorMessage>사용할 수 없는 유저아이디 입니다.</ErrorMessage>
                    <FormInput type="password" onChange={onChange} title="password" placeholder="Password" />
                    <ErrorMessage>사용할 수 없는 비밀번호 입니다.</ErrorMessage>
                    <FormInput type="password" onChange={checkPassword} title="password2" placeholder="Confirm Password" />
                    <ErrorMessage>입력하신 비밀번호가 일치하지 않습니다.</ErrorMessage>
                    <FormInput type="text" onChange={onChange} title="name" placeholder="Name" />
                    <ErrorMessage>이름은 한글 또는 영문만 가능합니다.</ErrorMessage>
                    <FormInput type="email" onChange={onChange} title="email" placeholder="Email" />
                    <ErrorMessage>이메일 형식에 맞지 않습니다.</ErrorMessage>
                    <FormBtn>회원가입</FormBtn>
                </SignupForm>
            </SignupBox>
        </Container>
    )
};

export default Signup;
