import React from 'react';
import {useState} from 'react';
import { Link } from 'react-router-dom';
import { authApi } from 'api';
import styled from "styled-components";

const Container = styled.div`
    background-color: #fafafa;
    width: 100%;
    height: calc(100vh - 60px);

    display: flex;
    justify-content: center;
    align-items: center;
`;
const SignupBox = styled.div`
    background-color: #fafafa;
    box-shadow: 0px 0px 3px 1px gray;

    width: 350px;
    height:550px;
    border-radius:20px;
    padding:0 40px 30px 40px;
    box-sizing: border-box;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
`;
const SignupTitle = styled.div`
    font-size: 35px;
    font-weight: 600px;
    font-style: italic;
    padding-top: 20px;
    margin-bottom: 30px;
`;
const SignupForm = styled.form`
    display: flex;
    flex-direction: column;
    justify-content: center;
    width:100%;
`;
const FormInput = styled.input`
    height: 35px;
    border: 2px solid gray;
    border-radius: 5px;
    text-align: center;
    &:nth-child(1){
        width: 75%;
        border-right: none;
        border-top-right-radius: 0;
        border-bottom-right-radius: 0;
    }
`;
const ErrorMessage = styled.div`
    height: 12px;
    margin: 5px 0;
    font-size: 12px;
    color:red;
    text-align: center;
`;
const FormBtn = styled.button`
    cursor: pointer;
    height: 35px;
    background-color: #fafafa;
    color: #0095f6;
    border:2px solid #0095f6;
    border-radius:5px;
    &:hover{
        color: #fafafa;
        background-color: #0095f6;
        box-shadow: 0px 0px 1px 1px #0095f6;
    }
`;
const DupCheckBtn = styled.button`
    background-color: #fafafa;
    color: #0095f6;

    width: 25%;
    height: 35px;
    border: 2px solid gray;
    border-left: none;
    border-radius: 5px;
    border-top-left-radius: 0;
    border-bottom-left-radius: 0;
    cursor: pointer;
    &:hover{
        color: #fafafa;
        background-color: #0095f6;
        border: 1px solid #0095f6;
        box-shadow: 0px 0px 1px 1px #0095f6;
    }
`;

const Signup = (props) => {
    const [state, setState] = useState({
        username: null,
        password: null,
        name:null,
        email:null
    });
    const [valid, setValid] = useState({
        username: false,
        name: false,
        password: false,
        email: false
    });
    const [error, setError] = useState({
        username: null,
        password: null,
        name:null,
        email:null
    });

    const onChange = (e) => {
        const { title, value } = e.target;
        setState({
            ...state,
            [title]: value  
        })
    }

    console.log(state);

    const checkPassword = (e) => {

    }

    const checkValid = async (e) => {
        const { title, value } = e.target;
        var regExp;
        var targetTagClass = '.'+title+'Valid';
        // eslint-disable-next-line default-case
        switch(title){
            case 'username':
                regExp = /^[A-Za-z]{1}[A-Za-z0-9_-]{3,19}$/; //영문시작 숫자+언더바/하이픈 + 4~20자리
                break;
            case 'password':
                regExp = /^(?=.*?[A-Z])(?=.*?[a-z])(?=.*?[0-9])(?=.*?[#?!@$%^&*-]).{8,}$/;
                break;
            case 'name':
                regExp = /^[가-힣a-zA-Z]+$/; //한글, 영문만 
                break;
            case 'email':
                regExp = /^[0-9a-zA-Z]([-_\.]?[0-9a-zA-Z])*@[0-9a-zA-Z]([-_\.]?[0-9a-zA-Z])*\.[a-zA-Z]{2,3}$/i;
                break;
        }

        const tag = document.querySelector(targetTagClass);
        let dupId=false;
        // username인 경우 서버로 value를 보내서 중복 확인 후 중복이 아니면 dupId = true, 중복시 dupId = false
        // 나머지는 dupId = true
        // if(title === 'username'){
        //     const result = await utilApi.duplication(value);
        //     console.log('check!!!!!!!!!!!!',result);
        //     if(result.data.code === 1){
        //         dupId = true;
        //         alert(result.data.message);
        //     } else {
        //         dupId = false;
        //         alert(result.data.message);
        //     }
        // } else {
        //     dupId = true;
        // }

        if(regExp.test(value) && dupId){
            e.target.style.border = '2px solid green';
            setValid({
                ...valid,
                [title]: true
            })
        } else {
            e.target.style.border = '2px solid red';
            setValid({
                ...valid,
                [title]: false
            })
        }
    }

    return (
        <Container>
            <SignupBox>
                <SignupTitle>Sign-up</SignupTitle>
                <SignupForm>
                    <label>Username</label>
                    <div>
                        <FormInput type="text" onChange={onChange} title="username" placeholder="Username" />
                        <DupCheckBtn>중복검사</DupCheckBtn>
                    </div>
                    <ErrorMessage>사용할 수 없는 유저아이디 입니다.</ErrorMessage>
                    <label>Password</label>
                    <FormInput type="password" onChange={onChange} onBlur={checkValid} title="password" placeholder="Password" />
                    <ErrorMessage>사용할 수 없는 비밀번호 입니다.</ErrorMessage>
                    <label>Confirm Password</label>
                    <FormInput type="password" onChange={checkPassword} title="password2" placeholder="Confirm Password" />
                    <ErrorMessage>입력하신 비밀번호가 일치하지 않습니다.</ErrorMessage>
                    <label>Name</label>
                    <FormInput type="text" onChange={onChange} onBlur={checkValid} title="name" placeholder="Name" />
                    <ErrorMessage>이름은 한글 또는 영문만 가능합니다.</ErrorMessage>
                    <label>Email</label>
                    <FormInput type="email" onChange={onChange} onBlur={checkValid} title="email" placeholder="Email" />
                    <ErrorMessage>이메일 형식에 맞지 않습니다.</ErrorMessage>
                    <FormBtn>회원가입</FormBtn>
                </SignupForm>
            </SignupBox>
        </Container>
    )
};

export default Signup;
