import React from "react";
import { Link, withRouter } from "react-router-dom";
import styled from "styled-components";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'

const Header = styled.div`
    background-color: #fafafa;
    position: fixed;
    width: 100%;
    height: 60px;
    padding: 0px 20px;
    font-size: 15px;
    display: flex;
    align-items: center;
    justify-content: space-between;
    box-sizing: border-box;
    z-index: 100;
`;
const HeaderIcon = styled.div`
    display: flex;
    justify-content: center;
    align-items:center;
    box-sizing:border-box;
    height: inherit;
    width:6%;
`;
const HeaderNav = styled.div`
    width: 85%;
    padding: 0px 20px;
`;
const NavUl = styled.ul`display:flex`;
const NavLi = styled.li`
    margin-left:20px;
    &:hover{
        color: lawngreen;
    }
`;
const cLink = styled(Link)``;
const HeaderMenu = styled.div`
`;

export default withRouter(({ location: {pathname}}) => (
    <Header>
        <HeaderIcon>
            <FontAwesomeIcon icon={["fab","slack"]} size="2x"/>
        </HeaderIcon>
        <HeaderNav>
            <NavUl>
                <NavLi>
                    <cLink to="/">Home</cLink>
                </NavLi>
                <NavLi>
                    <cLink to="/Challenge">Challenge</cLink>
                </NavLi>
                <NavLi>
                    <cLink to="/Dashboard">Dashboard</cLink>
                </NavLi>
                <NavLi>
                    <cLink to="/Mandalart">Mandalart</cLink>
                </NavLi>
            </NavUl>
        </HeaderNav>
        <HeaderMenu>
            <FontAwesomeIcon icon={["fas","user-circle"]} size="2x" />
        </HeaderMenu>
    </Header>
));

