import React from 'react';
import {useState} from 'react';
import { Link } from 'react-router-dom';
import { authApi } from 'api';
import styled from "styled-components";

const Container = styled.div`
    background-color: #fafafa;
    width: 100%;
    height: calc(100vh - 60px);

    display: flex;
    justify-content: center;
    align-items: center;
`;
const SignupBox = styled.div`
    background-color: #fafafa;
    box-shadow: 0px 0px 3px 1px gray;

    width: 350px;
    height:550px;
    border-radius:20px;
    padding:0 40px 20px 40px;
    box-sizing: border-box;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
`;
const SignupTitle = styled.div`
    font-size: 35px;
    font-weight: 600px;
    font-style: italic;
    padding-top: 20px;
    margin-bottom: 30px;
`;
const SignupForm = styled.form`
    display: flex;
    flex-direction: column;
    justify-content: center;
    width:100%;
`;
const FormInput = styled.input`
    height: 35px;
    border: 2px solid gray;
    border-radius: 5px;
    text-align: center;
    &:nth-child(1){
        width: 75%;
        border-right: none;
        border-top-right-radius: 0;
        border-bottom-right-radius: 0;
    }
`;
const ErrorMessage = styled.div`
    height: 12px;
    margin: 5px 0;
    font-size: 12px;
    text-align: center;
`;
const FormBtn = styled.button`
    cursor: pointer;
    height: 35px;
    background-color: #fafafa;
    color: #0095f6;
    border:2px solid #0095f6;
    border-radius:5px;
    &:hover{
        color: #fafafa;
        background-color: #0095f6;
        box-shadow: 0px 0px 1px 1px #0095f6;
    }
`;
const DupCheckBtn = styled.button`
    background-color: #fafafa;
    color: #0095f6;

    width: 25%;
    height: 35px;
    border: 2px solid gray;
    border-left: none;
    border-radius: 5px;
    border-top-left-radius: 0;
    border-bottom-left-radius: 0;
    cursor: pointer;
    &:hover{
        color: #fafafa;
        background-color: #0095f6;
        border: 1px solid #0095f6;
        box-shadow: 0px 0px 1px 1px #0095f6;
    }
`;

const Signup = (props) => {
    const [state, setState] = useState({
        username: null,
        password: null,
        name:null,
        email:null
    });
    const [valid, setValid] = useState({
        username: false,
        name: false,
        password: false,
        email: false
    });
    const [error, setError] = useState({
        username: null,
        password: null,
        name:null,
        email:null
    });
    /* 중복검사, confirm 통과시 true */
    const [check, setCheck] = useState({
        username: false,
        password: false
    })

    const onChange = (e) => {
        const { title, value } = e.target;
        setState({
            ...state,
            [title]: value  
        })
    }

    const checkDupl = async(e) => {
        e.preventDefault();
        const rawUsername = document.getElementsByClassName('duplication')[0].value;

        const result = await authApi.duplication(rawUsername);

        console.log(result);
        console.log('가공 ',result.data.code);
        // 중복됨

        let targetTag = null;
        if(result.data.code === -1){
            setMessage('duplication', false);
            targetTag = document.getElementsByClassName('duplication')[0];
            targetTag.style.color = '#EE2003';
            targetTag.style.border = '2px solid #ED2003';
            document.getElementsByClassName('username')[0].style.color = '#EE2003';
        } else {
            setMessage('username', true);

            targetTag = document.getElementsByClassName('duplication')[0];
            targetTag.style.color = '#00D904';
            targetTag.style.border = '2px solid #00D904';
            document.getElementsByClassName('username')[0].style.color = '#00D904';
        }

    }

    const checkPassword = (e) => {

    }

    const checkValid = async (e) => {
        const { title, value } = e.target;
        var regExp;
        var targetTagClass = '.'+title+'Valid';
        // eslint-disable-next-line default-case
        switch(title){
            case 'username':
                regExp = /^[A-Za-z]{1}[A-Za-z0-9_-]{3,19}$/; //영문시작 숫자+언더바/하이픈 + 4~20자리
                break;
            case 'password':
                regExp = /^(?=.*?[A-Z])(?=.*?[a-z])(?=.*?[0-9])(?=.*?[#?!@$%^&*-]).{8,}$/;
                break;
            case 'name':
                regExp = /^[가-힣a-zA-Z]+$/; //한글, 영문만 
                break;
            case 'email':
                regExp = /^[0-9a-zA-Z]([-_\.]?[0-9a-zA-Z])*@[0-9a-zA-Z]([-_\.]?[0-9a-zA-Z])*\.[a-zA-Z]{2,3}$/i;
                break;
        }

        if(regExp.test(value)){
            setValid({
                ...valid,
                [title]: true
            })
            if(title !== 'username'){
                setMessage(title, true);
                e.target.style.border = '2px solid #00D904';
                document.getElementsByClassName(title)[0].style.color = '#00D904';
            }

        } else {
            setValid({
                ...valid,
                [title]: false
            })
            setMessage(title, false);
            e.target.style.border = '2px solid #ED2003';
            document.getElementsByClassName(title)[0].style.color = '#EE2003';
        }
    }

    const setMessage = (title, result) => {
        let msg = null;
        // 성공 메세지
        if(result === true){
            // eslint-disable-next-line default-case
            switch(title){
                case 'username':
                    msg = '사용할 수 있는 Username 입니다.';
                    break;
                case 'password':
                    msg = '사용할 수 있는 Password 입니다.';
                    break;
                case 'name':
                    msg = '사용할 수 있는 Name 입니다.';
                    break;
                case 'email':
                    msg = '사용할 수 있는 Email 입니다.';
                    break;
            }

        // 실패 메세지
        } else {
            // eslint-disable-next-line default-case
            switch(title){
                case 'username':
                    msg = '영문시작, 영문/숫자/언더바/하이픈, 4~20자';
                    break;
                case 'password':
                    msg = '8자리 이상, 대문자, 소문자, 특수문자, 숫자 포함';
                    break;
                case 'name': 
                    msg = '한글, 영문';
                    break;
                case 'email':
                    msg = '이메일 형식';
                    break;
                case 'duplication':
                    msg = '중복된 아이디 입니다.';
                    title = 'username';
                    break;
            }
        }

        setError({
            ...error,
            [title]: msg
        })
    }

    const handleSubmit = async(e) => {

    }

    return (
        <Container>
            <SignupBox>
                <SignupTitle>Sign-up</SignupTitle>
                <SignupForm>
                    <label>Username</label>
                    <div>
                        <FormInput type="text" onChange={onChange} onBlur={checkValid}  title="username" />
                        <DupCheckBtn onClick={checkDupl}>중복검사</DupCheckBtn>
                    </div>
                    <ErrorMessage className="username">{error.username}</ErrorMessage>
                    <label>Password</label>
                    <FormInput type="password" onChange={onChange} onBlur={checkValid} title="password" />
                    <ErrorMessage className="password">{error.password}</ErrorMessage>
                    <label>Confirm Password</label>
                    <FormInput type="password" onChange={checkPassword} title="password2" />
                    <ErrorMessage></ErrorMessage>
                    <label>Name</label>
                    <FormInput type="text" onChange={onChange} onBlur={checkValid} title="name" />
                    <ErrorMessage className="name">{error.name}</ErrorMessage>
                    <label>Email</label>
                    <FormInput type="email" onChange={onChange} onBlur={checkValid} title="email" />
                    <ErrorMessage className="email">{error.email}</ErrorMessage>
                    <FormBtn>회원가입</FormBtn>
                </SignupForm>
            </SignupBox>
        </Container>
    )
};

export default Signup;
