import React from 'react';
import styled from "styled-components";

const Container = styled.div`
    background-color: #fafafa;
    width: 100%;
    height: calc(100vh - 60px);

    display: flex;
    justify-content: center;
    align-items: center;
`;

const LoginBox = styled.div`
    background-color: #fafafa;
    border: 1px solid gray;
    width: 350px;
    height:600px;
    border-radius:20px;
    padding:20px 40px;
    box-sizing: border-box;
    display: flex;
    flex-direction: column;
    align-items: center;
`;

const LoginTitle = styled.div`
    height: 10%;
    font-size: 35px;
    font-weight: 600px;
    font-style: italic;
    padding-top: 20px;
`;
/* 로그인 폼 */
const LoginForm = styled.div`
    display: flex;
    flex-direction: column;
    justify-content: center;
    height: 40%;
    width:100%;
`;
const FormInput = styled.input`
    height: 35px;
    border: 1px solid gray;
    border-radius: 5px;
`;
const ErrorMessage = styled.div`
    margin: 5px 0;
    font-size: 12px;
    color:red;
`;
const FormBtn = styled.button`
    height: 35px;
    background-color: white;
    color: #0095f6;
    border:1px solid #0095f6;
    border-radius:5px;
    &:hover {
        color: white;
        background-color: #0095f6;
    }
`;
/* horizon */
const Horizon = styled.div`
    border-top: 1px solid gray;
    height: 1px;
    width: 100%;
    margin-bottom:10px;
`;
/* Oauth */
const LoginOAuth = styled.div`

`;
/* Auth */
const LoginAuth = styled.div``;

const Login = (props) => {
    return (
        <Container>
            <LoginBox>
                <LoginTitle>Login</LoginTitle>
                <LoginForm>
                    <FormInput type="text" />
                    <ErrorMessage>이미 사용중인 Username 입니다.</ErrorMessage>
                    <FormInput type="password" />
                    <ErrorMessage>잘못된 Password 입니다.</ErrorMessage>
                    <FormBtn>SignIn</FormBtn>
                </LoginForm>
                <Horizon></Horizon>
                <LoginOAuth>

                </LoginOAuth>
            </LoginBox>
        </Container>
    )
}

export default Login;
